mapper.select:
select rt.review_id, rt.user_id, rt.travel_id, rt.review_title, rt.review_rating, rt.review_create_date,
rit.review_img_id, rit.temp_name,
min(st.schedule_date) as start_date, max(st.schedule_date) as end_date from review_tb rt left outer join review_img_tb rit on (rit.review_id = rt.review_id and rit.review_img_id = (select min(review_img_id) from review_img_tb _rit where _rit.review_id = rt.review_id)) left outer join schedule_tb st on (st.travel_id = rt.travel_id) where rt.user_id = #{userId} group by rt.review_id order by rit.review_img_id ASC, rt.review_create_date ASC;
